import { createGlobalStyle } from 'styled-components';

export default createGlobalStyle`
  *{ margin: 0; padding: 0; box-sizing: border-box; outline: none; }

  body {
    font-family: 'Poppins', sans-serif;
  }

  input, select, button {
    font-family: 'Poppins', sans-serif;
  }

  button { cursor: pointer; }
`;
